	public static void write_SPSetInfo(Packet p, GameUser u, int cType, int cPromotion) {
		Connection con = Database.getConnection();
 		PreparedStatement ps = null;
 		ResultSet rs = null;
 		
		p.writeInt(2); // vector<SPSetInfo> 크기
		
		p.writeInt(0); // - 세트 ID
		
 		try {
 			ps = con.prepareStatement("SELECT * FROM `skill` WHERE `LoginUID` = ? AND `CharType` = ? AND `CharPromotion` = ? AND `SkillSetID` = ?"
 					                 , ResultSet.TYPE_SCROLL_INSENSITIVE
 					                 , ResultSet.CONCUR_UPDATABLE);
 			ps.setInt(1, u.LoginUID);
 			ps.setInt(2, cType);
 			ps.setInt(3, cPromotion);
 			ps.setInt(4, 0); // 스킬 세트 ID
 			rs = ps.executeQuery();
 			
 			if( rs.last() == true ) {
 	 			// 커서를 뒤로 옮기고, 결과가 있다면
 				int rowCount = rs.getRow(); // 결과 개수를 기록하고
 				rs.first();                 // 커서를 앞으로 옮긴다
 				
 				p.writeInt(rowCount); // 사이즈 
 				
 				do {
 					int slotIndex = rs.getInt("SlotIndex");
 					int slotID = rs.getInt("SlotID");
 					int skillID = rs.getInt("SkillID");
 					
 					p.writeInt(slotIndex);
 					p.writeInt(slotID);
 					p.writeInt(skillID);
 				}while( rs.next() == true );
 			} else {
 				// 결과가 없다면 0 전송
 				p.writeInt(0); // 사이즈
 			}
 		}catch(Exception e) {
 			e.printStackTrace();
 		} finally {
 			Database.close(null, ps, rs);
 		}
		
		p.writeInt(1); // - 세트 ID
		
		try {
 			ps = con.prepareStatement("SELECT * FROM `skill` WHERE `LoginUID` = ? AND `CharType` = ? AND `CharPromotion` = ? AND `SkillSetID` = ?"
 					                 , ResultSet.TYPE_SCROLL_INSENSITIVE
 					                 , ResultSet.CONCUR_UPDATABLE);
 			ps.setInt(1, u.LoginUID);
 			ps.setInt(2, cType);
 			ps.setInt(3, cPromotion);
 			ps.setInt(4, 1); // 스킬 세트 ID
 			rs = ps.executeQuery();
 			
 			if( rs.last() == true ) {
 	 			// 커서를 뒤로 옮기고, 결과가 있다면
 				int rowCount = rs.getRow(); // 결과 개수를 기록하고
 				rs.first();                 // 커서를 앞으로 옮긴다
 				
 				p.writeInt(rowCount); // 사이즈 
 				
 				do {
 					int slotIndex = rs.getInt("SlotIndex");
 					int slotID = rs.getInt("SlotID");
 					int skillID = rs.getInt("SkillID");
 					
 					p.writeInt(slotIndex);
 					p.writeInt(slotID);
 					p.writeInt(skillID);
 				}while( rs.next() == true );
 			} else {
 				// 결과가 없다면 0 전송
 				p.writeInt(0); // 사이즈
 			}
 		}catch(Exception e) {
 			e.printStackTrace();
 		} finally {
 			Database.close(null, ps, rs);
 		}
		
		Database.close(con, ps, rs);
	}
	
